props.code:
# layout.styl
*
    margin 0
    padding 0

body
    width 100vw
    height 100vh

::-webkit-scrollbar
    display: none
@import "_partial/header"
@import "_partial/footer"
@import "index"
@import "post"

# 新建 css/archive.ejs

.archive {
    margin: 1em auto;
    padding: 30px 50px;
    background-color: #fff;
    border: 1px solid #ddd;
    box-shadow: 0 0 2px #ddd;
    .post-archive {
        list-style: none;
        padding: 0;
        .post-item {
            margin: 5px 0;
            .post-date {
                display: inline-block;
                margin-right: 10px;
                color: #BABABA;
            }
            .post-title {
                color: #368CCB;
                text-decoration: none;
            }
        }
    }
}
