.Parent {
    width: 100vw;
    height: 100vh;
    display: none;
    background-color: rgba(0, 0, 0, 0.8);
}

.Daughter {
    width: 300px;
    height: 300px;
    position: absolute;
    top: 0;
    left: 0;
    background-color: #FFFFFF;
    border-radius: 300px;
    background: url('需要显示的图片');
    background-position: 100px 100px;
    background-repeat: no-repeat;
}

// 屏蔽默认的浏览器自带右键
window.oncontextmenu = function (e) {
    e.preventDefault();
};

const Parent = document.getElementById("Parent");
const Daughter = document.getElementById("Daughter");
const body = document.body;
// const audio = new Audio('音效链接');

// 按下右键时显示聚光灯, 否则隐藏
window.onmousedown = function (e) {
    if (e.button == 2) {
        Parent.style.display = "block";
        body.style.cursor = 'none';
        // audio.play()
    } else {
        Parent.style.display = "none";
        body.style.cursor = 'auto';
    }
};

// 聚光灯跟随鼠标以及背景移动逻辑
document.onmousemove = function (event) {
    let left = event.clientX;
    let top = event.clientY;
    Daughter.style.left = left - 100 + "px";
    Daughter.style.top = top - 100 + "px";
    Daughter.style.backgroundPosition = "-" + left + "px " + "-" + top + "px";
};
